#include "LineContainer.h"using namespace cocos2d;LineContainer::LineContainer() {}LineContainer *LineContainer::create(){    LineContainer *obj = new LineContainer();    if ( obj && obj->init() )    {        obj->autorelease();        return obj;    }        CC_SAFE_DELETE( obj );    return obj = nullptr;}bool LineContainer::init(){    if (!DrawNode::init())    {        return false;    }        _dash = 10;    _dashSpace = 10;    _lineType = LINE_NONE;    _screenSize = Director::getInstance()->getWinSize();    _energyLineX = _screenSize.width * 0.96f;    _energyHeight = _screenSize.height * 0.8f;        this->reset();    this->scheduleUpdate();        return true;}void LineContainer::reset() {    _energy = 1.0;    _energyDecrement = 0.005f;}void LineContainer::update (float dt) {        _energy -= dt * _energyDecrement;    if (_energy < 0) _energy = 0;    }void LineContainer::setEnergyDecrement(float value) {    _energyDecrement += value;}		