#include "Rocket.h"Rocket::Rocket(void)  :_targetRotation(0),_dr(0),_ar(0),_vr(0),_rotationSpring(0.1f),_rotationDamping(0.8f),_pivot(Point(-1,-1)),_rotationOrientation(ROTATE_NONE){}Rocket::~Rocket(void){}Rocket* Rocket::create () {	auto sprite = new Rocket();    	if (sprite && sprite->initWithSpriteFrameName("rocket.png")) {        sprite->reset();		sprite->setRadius(sprite->boundingBox().size.height * 0.5f);        sprite->autorelease();                return sprite;	}	CC_SAFE_DELETE(sprite);	return nullptr;}void Rocket::reset () {        _speed = 80;	_pivot = Vec2(-1,1);    _rotationOrientation = ROTATE_NONE;    this->setRotation(-90);	_targetRotation = -90;    float angle = CC_DEGREES_TO_RADIANS(this->getRotation());	_vector = Vec2 (_speed * cos( angle ), -_speed * sin( angle ));}			void Rocket::update (float dt) {    }void Rocket::select(bool flag) {    if (flag) {        this->setDisplayFrame(SpriteFrameCache::getInstance()->getSpriteFrameByName("rocket_on.png"));    } else {        this->setDisplayFrame(SpriteFrameCache::getInstance()->getSpriteFrameByName("rocket.png"));    }}bool Rocket::collidedWithSides() {        Size screenSize = Director::getInstance()->getWinSize();            if (_position.x > screenSize.width - _radius) {        _position.x =  screenSize.width - _radius;        _rotationOrientation = ROTATE_NONE;        _vector = Vec2(this->getVector().x * -1, this->getVector().y);        this->setRotationFromVector();        return true;    }        if (_position.x < _radius) {        _position.x =  _radius;       	_rotationOrientation =  ROTATE_NONE;        _vector =  Vec2 (this->getVector().x * -1, this->getVector().y);        this->setRotationFromVector();        return true;            }        if (_position.y < _radius) {        _position.y = _radius;       	_rotationOrientation = ROTATE_NONE;        _vector =  Vec2 (this->getVector().x, this->getVector().y * -1);        this->setRotationFromVector();        return true;    }        if (_position.y > screenSize.height - _radius) {        _position.y =  screenSize.height - _radius;       	_rotationOrientation = ROTATE_NONE;        _vector =  Vec2 (this->getVector().x, this->getVector().y * -1);        this->setRotationFromVector();        return true;    }        return false;}